! function(n) {
	n(document).ready(function() {
		n("#lead-content").fadeIn(3000);
	}), n(".top").click(function() {
        n("html, body").animate({
            scrollTop: 0
        }, 500)
    }), n("#lead-down span").click(function() {
        var e = n("#lead").next().offset().top;
        n("html, body").animate({
            scrollTop: e + "px"
        }, 500)
    })
}(jQuery);