function fIn(element) {
    var opa = 0.1; 
    element.style.display = 'block';
    var timer = setInterval(function () {
        if (opa >= 1){
            clearInterval(timer);
        }
        element.style.opacity = opa;
        opa += opa * 0.1;
    }, 100);
}

function topTo() {
  document.body.scrollTop = 0;
  document.documentElement.scrollTop = 0;
} 

